mapper.select:
SELECT * FROM NOTICE_BOARD NB LEFT JOIN EMPLOYEE E ON (NB.EMP_NUM = E.EMP_NUM) LEFT JOIN DEPARTMENT D ON (E.DEPT_NUM = D.DEPT_NUM)
WHERE D.DEPT_NUM = #{deptNum}
ORDER BY NOTICE_NUM DESC LIMIT #{startRow}, #{perPage}

mapper.update:
UPDATE NOTICE_BOARD
	SET NOTICE_TITLE= #{noticeTitle}, NOTICE_CONTENTS= #{noticeContents} 
WHERE NOTICE_NUM = #{noticeNum}
